var w = $("#"+id);
var username = false;


function get_session_cookie() {
    return document.cookie.replace(/(?:(?:^|.*;\s*)SESSION\s*\=\s*([^;]*).*$)|^.*$/, "$1");
}

function logout_and_redirect(url) {
    // remove session cookie
    document.cookie='SESSION=';

    // redirect after a timeout
    setTimeout(function() {
        w.trigger('auth');
        //location.reload(true);
        location.assign(url || '/');
    }, 1000);
}

endpoint.schema('endpoint').table('current_user').row()
.then(function(row) {

    var role_name = row.get('current_user');
    var role_id = { name: role_name };
    console.log('role name', role_name);

    if (role_name == 'anonymous') {
        throw 'Not logged in';
    }
    if (role_name == 'postgres') {
        alert('You\'re logged in as postgres, that\'s not right, call mickey');
    }

    return endpoint.schema('endpoint').table('user').row('role_id', role_id);

}).then(function(row) {
    username = row.get('name') || row.get('email');
    w.find('.username').html(username);
}).catch(function(e) {
    console.log('not logged in:', e);
    logout_and_redirect();
});



w.find('.logout').click(function() {

    var cookie = get_session_cookie('SESSION');
    if (cookie != null) {

        // there exists a session cookie
        // look for session in table
        endpoint.schema('endpoint').table('session').row('id', cookie)
        .then(function(session) {

            if(!session) {
                // not logged in
                return logout_and_redirect();
            }
            session.delete().then(function() {
                // logged out now
                // redirect
                logout_and_redirect();
            });
        });

    }
    else {
        // there is no session cookie
        logout_and_redirect();
    }
});

