    return w.find('.workspace_tab_header.highlighted').first().data('tab_id');
}

function show_tab(tab_id) {
    // console.log('show tab ', tab_id);
    // show the tab
    w.find('.workspace_tab').each(function(e) {
        if ($(this).data('tab_id') == tab_id) {
            $(this).show();
        } else {
            $(this).hide();
        }
    })

    // highlight the tab header
    w.find('.workspace_tab_header').each(function(e) {
        if ($(this).data('tab_id') == tab_id) {
            $(this).addClass('highlighted');
        } else {
            $(this).removeClass('highlighted');
        }
    })
}

function close_tab(tab_id) {
    /*
    why doesn't this work?
    w.find('.workspace_tab[data-tab_id="'+tab_id+'"]').remove();
    w.find('.workspace_tab_header[data-tab_id="'+tab_id+'"]').remove();
    */

    var current_tab_closed = false;
    if (tab_id == current_tab()) {
        current_tab_closed = true;
    }

    // remove the tab
    w.find('.workspace_tab').each(function(e) {
        if ($(this).data('tab_id') == tab_id) {
            $(this).remove();
        }
    });

    // remove the tab header
    w.find('.workspace_tab_header').each(function(e) {
        if ($(this).data('tab_id') == tab_id) {
            $(this).remove();
        }
    });

    // if the current tab is closed, replace it with another
    if (current_tab_closed) {
        var last_tab = w.find('.workspace_tab').last();
        if (last_tab) {
            show_tab(last_tab.data('tab_id'));
        }
    }
}

function tab_is_open(tab_id) {
    // check to see if it's already open
    var is_open = false;
    var tabs = w.find('.workspace_tab');
    for (var i=0;i<tabs.length;i++) {
        var this_tab_id = $(tabs[i]).data('tab_id');

        // console.log('comparing '+this_tab_id +' to ' + tab_id);
        if (this_tab_id == tab_id) {
            // console.log('already open');
            return true;
        }
    };
    return false;
}

