endpoint.schema('meta').table('column').row('id', datum.id)
.then(function(datum) {

    // TODO: Replace this with a regex that capitalizes the beginning of every word
    var name = datum.get('name').replace('_', ' ');
    var label = name.slice(0, 1).toUpperCase() + name.slice(1);
    w.find('.label').text(label);

    w.data('valid', (datum.get('nullable') || datum.get('default') != null));
    w.data('name', datum.get('name'));

    endpoint.schema('ide').table('foreign_key').row('from_column_id', datum.get('id'))
    .then(function(fkey) {

        var col_id = fkey.get('to_column_id');
        var fkey_rows = endpoint.schema(col_id.relation_id.schema_id.name).table(col_id.relation_id.name).rows({
            include: ['id', 'purpose'],
            order_by: {
                column: 'purpose'
            }
        });

        widget.sync(fkey_rows, w.find('select'), function(row) {
            return widget('core:option', {
                label: row.get('purpose'),
                value: row.get('id'),
                selected: row.get('id') === datum.get('default')
            });
        });
    });

    // EVENT BINDINGS
    w.find('select').on('change', function(e) {
        // set value to the input
        w.data('value', $(this).val());
        w.data('valid', true);
    });

});

