/*
 * ARGUMENTS
 * -----------
 * context_data: {Object} for compatibility with sem_list_item_new
 * from_row: {Row} row to join
 * from_column: {String} fkey column of from_row
 * to_relation: {Table} relation to join to
 * to_column: {String} column of to_relation fkey references
 * order_by_function: {Function}
 *	- takes a rowset and returns the rowset after performing order_by clause
 * label_function: {Function}
 *	- takes a to_row and returns the human-readable label
 * new_enabled: {Boolean} whether to include 'new row' functionality
 */

var w = $("#"+id);


var append_new_widget = function() { 

    alert('New widget still loading...\nTry again in a moment'); 

}

if (typeof context_data == 'undefined') {
    context_data = {};
}

if (typeof order_by_function == 'undefined') {
    order_by_function = function(rows) {
        return rows;
    };
}

if (typeof label_function == 'undefined' || label_function == false) {
    label_function = function(to_row) {
        return to_row.get('name');
    };
}


// Find semantic override for sem_list_item_new
endpoint.schema('meta').view('relation').row().where({
    schema_name: to_relation.schema.name,
    name: to_relation.name
}).result(function(to_relation_obj) {

    var new_row_widget = $(document).find('.semantics_manager').data('get_relation_field')(to_relation_obj.get('id'), 'new_row_widget_id');

    if (new_row_widget == null) {
        new_row_widget = 'sem_list_item_new';
    }

    append_new_widget = function() {

        w.append(
            widget('modal_overlay', {
                content: widget(new_row_widget, { 
                    relation: to_relation_obj,
                    context_data: context_data
                })
            })
        );

    }


});


// Prevent reload tab triggered by 'sem_list_item_new'
w.on('reload_tab', function(e) {
    e.stopPropagation();
});


// When new_row_widget returns...
w.on('new_row', function(e, payload) {

    select_row(payload.new_row);

});


// When a row is selected...
function select_row(row) {

    if (row == 'new') {

        append_new_widget();

    }

    else if (row == 'none') {

        from_row.set(from_column, null);
        from_row.update().done(function() {

            console.log('Fkey deleted');
            w.trigger('fkey_deleted');
            w.trigger('done');

        });

    }

    else {

        from_row.set(from_column, row.get(to_column));
        from_row.update().done(function() {

            console.log('Fkey updated');
            w.trigger('fkey_updated', row);
            w.trigger('done');

        });

    }


}


// Prepare autocomplete
order_by_function(to_relation.rows()).done(function(rows) {

    var ps2 = Array();
    var start = 0;

    ps2.push({
        value: '[ none ]',
        data: 'none'
    });

    ++start;

    if (new_enabled) {
        ps2.push({
            value: '[ new ]',
            data: 'new'
        });

        ++start;
    }

    for (var i = 0; i < rows.length; i++) {

        ps2[start + i] = { value: label_function(rows[i]), data: rows[i] }

    }


    w.find('.autocomplete').autocomplete({
        lookup: ps2,
        appendTo: '.results',
        onSelect: function (suggestion) {
            //alert('You selected: ' + suggestion.value + ', ' + suggestion.data);
            select_row(suggestion.data);

        }
    });  

    load_complete();
});

function load_complete() {

    w.find('.loading').hide();
    w.find('.loaded').show();
    console.log('Ready for autocomplete');

}

