var w = $("#"+id);
var tab_links = w.find('> nav > a');
var content = w.find('.content');

// tab colors
tab_links.each(function() {
    var tab_name = $(this).attr('data-tab');
    if (typeof tab_colors != 'undefined' && typeof tab_colors[tab_name] != 'undefined') {
	    $(this).css('border-left', '10px solid ' + tab_colors[tab_name]);
    }
});


tab_links.on('click', function() {
    tab_links.removeClass('selected');
    var tab_name = $(this).attr('data-tab');
    w.data('selected_tab', $(this));

    content.html(tabs[$(this).addClass('selected').attr('data-tab')]());
    if (typeof tab_colors != 'undefined' && typeof tab_colors[tab_name] != 'undefined') {
        w.css('border-left', '10px solid '+tab_colors[tab_name]);
    }
});


if (typeof selected == 'undefined') {
    tab_links.filter(':first').get(0).click();
}
else {
    tab_links.filter('[data-tab="' + selected + '"]').get(0).click();
}

