var w = $("#"+id);

var rel_name = datum.schema.name + '.' + datum.name;
w.find('.rel_name').text(rel_name);

function save(save_as_null) {
    var val = w.find('.name').val().trim();

    if (save_as_null) {
        val = null;
    }

    datum.insert({
        name: val
    })
    .then(function() {
        w.trigger('status', ['New row on ' + rel_name, 'success']);
        w.trigger('done');
    }).catch(function() {
        w.trigger('status', ['Failed insert on ' + rel_name, 'fail']);
    });
}


/* TRIGGERS */
w.find('.save_as_null').on('click', save.bind(this, true));

w.find('.name').on('keyup', function(e) {
    e.stopPropagation();
    if (e.key == 13) {
        save(false);
    }
});

w.find('.save').on('click', save.bind(this, false));
w.on('keydown', jwerty.event('ctrl+s', save.bind(this, false)));

