var has_first = false;
for (var i=0;i<tabs.length;i++) {
    var tab = tabs[i];

    // get color
    var color = '';
    if (typeof tab.color != 'undefined')
        color = tab.color;

    // append tab
    $('<a>'+tab.label+'</a>')
    .appendTo(w.find('nav'))
    .css('border-left', '10px solid ' + color)
    .data('tab_position', i)
    .click(function() {
        var i = $(this).data('tab_position');        
        open_tab(i);
    });

    var content = $('<div class="content not_loaded"></div>')
	    .css('border-left', '10px solid ' + color)
    	.appendTo(w.find('.content_pane'))
    	.hide()

    // show if necessary
    if (typeof tab.show_first != 'undefined' && tab.show_first && !has_first) {
        has_first = true;
        open_tab(i);
    }
}

if (!has_first) {
    open_tab(0);
}

function open_tab(i) {
    // jquery objects for tab and content pane
    var tab = w.find('> nav a').eq(i);
    var content_pane = w.find('> .content_pane > div').eq(i);

    // hide all tabs then select this one
    w.find('> nav a').removeClass('selected');
    tab.addClass('selected');

    // hide all content panes and show this one
    w.find('> .content_pane > div').hide();
    content_pane.show();

    // load contents if needed
    if (content_pane.hasClass('not_loaded')) {
        // append content
        if (typeof tabs[i].content == 'function') {
            tabs[i].content = tabs[i].content();
        }
        content_pane
        .html(tabs[i].content)
        .removeClass('not_loaded');
    }
}

