    // get color
    var color = '';
    if (typeof tab.color != 'undefined')
        color = tab.color;

    // append tab
    $('<a>'+tab.name+'</a>')
    .appendTo(w.find('nav'))
    .css('border-left', '10px solid ' + color)
    .data('tab_position', i)
    .click(function() {
        var i = $(this).data('tab_position');
        console.log('i = '+i);
        w.find('.content_pane > div').hide();
