var w = $('#'+id);

/*
args:
	items: Array || Object (that supports forEach)
    item_name: String function(item)
    item_detail: html function(item)
*/

// append this items to the list

if (typeof items == 'Array') {
    for (var i=0;i<items.length;i++) {
        w.find('.list').append(widget('layout:list_view_li', {
            item_name: item_name,
            item: items[i],
            item_index: i
        }));
    }
} else {
    items.forEach(function(item, i) {
        w.find('.list').append(widget('layout:list_view_li', {
            item_name: item_name,
            item: item,
            item_index: i
        }));        
    });
}

// bind the select event handler
w.bind('item_select', function(e,o) {
    w.find('.detail').html(item_detail(o.item))
});

// COLLAPSABLE NAV
var divider_disabled = false;
function close_divider() {
    if (!divider_disabled) {
        w.find('> nav.list_container').hide(500);
        w.find('> .divider').html('&gt;').addClass('collapsed').removeClass('expanded');
    }
}
function open_divider() {
    if (!divider_disabled) {
        w.find('> nav.list_container').show(500);
        w.find('> .divider').html('&lt;').addClass('expanded').removeClass('collapsed');
    }
}
function toggle_divider() {
    if (w.find('> nav.list_container').is(':visible')) {
        close_divider();
    }
    else {
        open_divider();
    }
}
w.find('.divider').click(toggle_divider);
