.{{= name }} {
}

.{{= name }} > header {
    text-align: center;
}


.ui.menu .item img.logo {
    margin-right: 1.5em;
}
.main.container {
    margin-top: 5em;
}
.wireframe {
    margin-top: 2em;
}
.ui.footer.segment {
    margin: 5em 0em 0em;
    padding: 5em 0em;
}

.{{= name }} > header {
    text-align: center;
}

.{{= name }} > div.segment {
    display: none;
}

var w = $("#"+id);

w.find('.tabular.menu .item').tab();

w.find('button.configure').click(function() {
    w.find('div.setup').hide();
    w.find('div.setup_custom').show();

});

w.bind('connection_cancel', function() {
    w.find('div.setup').show();
    w.find('div.setup_custom').hide();
});

.{{= name }} > header {
    text-align: center;
}

.{{= name }} div.setup_custom {
    display: none;
}
