var w = $('#'+id);

w.find('.content').append(content);

function dismiss(e) {
    e.stopPropagation();
    w.fadeOut('fast').remove(); 
    return false;
}

w.on('done dismiss close_modal', dismiss);
w.find('button.close').click(dismiss);

if (typeof click_close != 'undefined' && click_close) {
    w.find('.content').on('click', function(e) {
        e.stopPropagation();
    });
}
jwerty.key('esc', dismiss);

var w = $("#"+id);

// inputs: type (html/css/javascript/...)

w.find('button.new_snippet').click(function() {
    w.append(widget('ide:modal_pretty', {
        content: widget('code_editor_snippet_new')
    }));
});


function reload() {
    w.find('.buttons').empty();
    endpoint.schema('widget').table('snippet').rows({
        where: {
            name: 'type',
            op: '=',
            value: snippet_type
        }
    }).then(function(snippets) {
        widget.sync(snippets, w.find('.buttons'), function(snippet) {
            return widget('code_editor_snippet_button', {
                snippet: snippet
            });
        });
    });
}
w.bind('snippets_reload', reload);
reload();

var w = $("#"+id);

// Row detail tabbed
/* Originally created for widget editing
 * - id field is ignored
 * - name field is editing above, such as editable_field
 * - the rest of fields are edited in tabs
 */

/* ICONS */
// FIXME: gear icons hack, cause unicode breaks save
var icons = {
    'code': 9881,
    'data': 9883
};

for (var i in icons) {
    w.find('.'+i).append('<div class="icon">&#' + icons[i] + ';</div>');
}

// TODO this fails for views
row.relation.row(row.pk_column_name, row.pk_value)
.then(function(row) {

    /* SETUP */
    var rel_name = row.relation.schema.name + '.' + row.relation.name;
    var relation = row.relation;

    var tabs = {};
    var tab_colors = {};
    var columns = [];
    var colors = ['red', 'orange', 'yellow', 'green', 'blue', 'indigo', 'violet'];

    if (row.columns == null) {
        throw 'Please call this row with "meta_data" set to true. ' + rel_name;
    }
    row.columns.forEach(function(col, i) {
        if (col.name != 'id' && col.name != 'name') {
            tabs[col.name] = function() {
                return widget('semantics/form_field', row.field(col.name));
            };
            tab_colors[col.name] = colors[i % colors.length];
            columns.push(col.name);
        }
    });


    /* TABBED LAYOUT */
    var preview_row = row.clone();

    w.find('> .top > .section_content > .tabs').append(
        widget('ide:tabbed_layout', {
            tabs: tabs,
            tab_colors: tab_colors
        })
    );

    function handle_delete_button() {
        if(confirm("Are you sure you\'d like to delete this row?")) {
            row.delete()
            .then(function() {
                w.trigger('status', ['Row deleted from ' + rel_name, 'success']);
                w.trigger('row_detail_delete');
                w.trigger('done');
                w.remove();
            }).catch(function() {
                w.trigger('status', ['Failed row delete on ' + rel_name, 'fail']);
                w.trigger('done');
            });
        }
    }


    function set_have_unsaved_changes(is_unsaved){
        /*
    var save_button = w.find("> .save");
    save_button.text(is_unsaved ? 'Save' : 'Saved');
    window.onbeforeunload = is_unsaved ? function() { return "You have unsaved changes!" } : null;
    */
    } 



    function handle_save_button() {

        update_row()
        .then(function() {
            set_have_unsaved_changes(false);
            w.trigger('status', ['Widget saved', 'success']);
        }).catch(function() {
            w.trigger('status', ['Widget delete failed', 'fail']);
            setTimeout(set_have_unsaved_changes.bind(this,false), 2000)
        });

    }



    function update_row() {

        row.set('pre_js',  row.get('pre_js'));
        row.set('css',     row.get('css'));
        row.set('html',    row.get('html'));
        row.set('post_js', row.get('post_js'));

        return row.update();

    }

    w.find('button.save').on('click', handle_save_button);
    w.on('keydown', jwerty.event('ctrl+s', handle_save_button));
    w.find('button.delete').on('click', handle_delete_button);

    w.find('button.copy').click(function() {

        if (confirm('Copy this row?')) {

            var cloned_row = row.clone();
            var new_name = row.get('name') + '_COPY';

            cloned_row.set('id', undefined);
            cloned_row.set('name', new_name);


            relation.insert(cloned_row.row_data)
            .then(function(new_row) {
                w.trigger('status', ['Row copied from ' + rel_name, 'success']);
                if (confirm('Would you like to edit the new row now?\nPress cancel to resume editing original')) {
                    console.log('isnerted row', new_row);
                    w.replaceWith(
                        widget('semantics/row_detail', new_row)
                    );
                }
            }).catch(function(xhr) {
                w.trigger('status', ['Row copy failed on ' + rel_name, 'fail']);
            });
        }

    });
});

