.{{= name }} {
    width: 100px;
    padding-left: 5px;
    margin-bottom: -1px;
    margin-right: 3px;
    color: black;
    background-color: #eee;
}

.{{= name }} .close {
    padding: 0px 5px;
}

.{{= name }}:hover {
    cursor: pointer;
    background-color: #044;
}

.{{= name }}.highlighted {
    background-color: #077;
    color: white;
}

.{{= name }}.behind {
    background-color: #0bb;
    color: white;
}

.{{= name }} span.close:hover {
    background-color: red;
    cursor: pointer;
}

var w = $('#'+id);

w.data('datum', row);
w.data('row', row);

// Assign all triggers
['click'].forEach(function(trig) {
    w.on(trig, function(e) {
        e.stopPropagation();
        w.trigger('list_item_' + trig, [row, w]);
    });
});

var w = $("#"+id);

////////////////////////////////////////////////////////////////////
// clearable timeout - http://stackoverflow.com/questions/5226578/check-if-a-timeout-has-been-cleared
////////////////////////////////////////////////////////////////////

function Timeout(fn, interval, scope, args) {
    scope = scope || window;
    var self = this;
    var wrap = function(){
        self.clear();
        fn.apply(scope, args || arguments);
    }
    this.id = setTimeout(wrap, interval);
}


Timeout.prototype.id = null
Timeout.prototype.cleared = false;
Timeout.prototype.clear = function () {
    clearTimeout(this.id);
    this.cleared = true;
    this.id = null;
};

////////////////////////////////////////////////////////////////////
// draw the tree
////////////////////////////////////////////////////////////////////

function draw_tree() {
    if (timer && !timer.cleared) {
        timer.clear();
    }


    var widget_tree = get_widget_tree($('body')[0]);
    var agg_tree = get_aggregate_widget_tree(widget_tree);

    var c = w.find('.tree_container').empty();

    for (var widget_name in agg_tree) {

        c.append(widget('debugger_widgets_tree', { 
            widget_name: widget_name,
            properties: agg_tree[widget_name],
            depth: 0
        }));
    }
}

function delayed_redraw(e, payload) {
    var widg = payload.widget.get(0);
    // don't redraw for any debugger widget loadings, lest we blow up the world.
    if (widg.className.indexOf('debugger_widget') != -1) return;

    if (!timer || timer.cleared) {
        // console.log('setting up delayed redraw....'+widg.className);
        timer = new Timeout(draw_tree, 100);
    }
    else { 
        // console.log('.....no go on '+widg.className); 
    }
}

var timer = false;
draw_tree();
$(document).bind('widget_loaded', delayed_redraw);



/////////////////////////////////////////////////////////////////////
// show all
/////////////////////////////////////////////////////////////////////

w.find('div.show_all').bind('mouseover', function(e) {
    $("[data-widget]").addClass('debugger_widget_highlight');
    e.stopPropagation();    
}).bind('mouseout', function(e) {
    $("[data-widget]").removeClass('debugger_widget_highlight');
    e.stopPropagation();
});

var w = $("#"+id);

// get the real row
endpoint.schema(change.get('row_id').schema_name)
.table(change.get('row_id').relation_name)
.row(change.get('row_id').pk_column_name, change.get('row_id').pk_value)
.then(function(real_row) {

    // for each offstage change (only!  for now), show the diff

    for (var i=0;i<change.get('offstage_field_changes').length;i++) {
        w.find('.diffs').append(widget('stage_field_changed_diff', {
            staged: true,
