var w = $('#'+id);

function display() {
    var val = JSON.stringify(field.value);

    if (val == null) {
        val = 'NULL';
    }

    else if (val == '') {
        val = '____';
    }

    w.find('.display').html(val);
    w.find('input').val(val);

    w.removeClass('saving')
    .removeClass('editing')
    .addClass('displaying');

    //w.find('.edit input').prop('disabled', false);
}
display();

function edit() {
    w.removeClass('displaying')
    .removeClass('saving')
    .addClass('editing');
    w.find('input').focus();
}


function save(saving_as_null) {

    w.removeClass('displaying')
    .removeClass('editing')
    .addClass('saving');

    //w.find('.edit input').prop('disabled', true);

    var old_value = field.value;

    // save
    if (saving_as_null) {
        field.set(null);
    }
    else {
        field.set(w.find('input').val());
    }

    field.update().then(function() {
        w.trigger('status', ['Saved', 'success']);
        w.trigger('form_field_save');
        display();

    }).catch(function(error) {
        field.set(old_value);
        w.trigger('status', ['Failed save', 'fail']);
        display();
    });
}


// EVENT BINDINGS
w.find('.save').on('click', save.bind(this, false));
w.find('.save_as_null').on('click', save.bind(this, true));
w.find('.display').on('click', edit);
w.find('.cancel').on('click', display);
w.find('input').on('keyup', function(e) {
    if (e.keyCode == 27) {
        display();
    }
    if (e.keyCode == 13) {
        save(false);
    }
});

