// trigger:snippet_inject handler

// https://stackoverflow.com/questions/23733455/inserting-a-new-text-at-given-cursor-position
function insertString(editor,str){
    var selection = editor.getSelection();
    if(selection.length>0){
        editor.replaceSelection(str);
    }
    else{
        var doc = editor.getDoc();
        var cursor = doc.getCursor();
        var pos = {
            line: cursor.line,
            ch: cursor.ch
        }
        doc.replaceRange(str, pos);
        // doc.setSelection(pos,???); not sure select the injected text, wish it was a flag
    }
}
function getSelectedRange() {
    return { from: editor.getCursor(true), to: editor.getCursor(false) };
}

function autoFormatSelection() {
    var range = getSelectedRange();
    editor.autoFormatRange(range.from, range.to);
}
w.parent().bind('snippet_inject', function(e,o) {
    insertString(editor, o.snippet.get('snippet'));
    editor.focus();
    autoFormatSelection()
});





