// ganked (unironically) from ide:row_list_row

/*
 * FIXME:  Display logic is wrong here.  Two conflated ideas:
 * What is type of the change, if any, on the stage?
 * What is the state of the row in the working copy?
 *
 * Much confused.  Rework the icon logic here.
 * 
 */




// change type icon
var change = commit_row.get('change_type');
if (change != 'same') {
    if (change == 'added')
	    w.find('.added').show();
    else if (change == 'deleted')
	    w.find('.deleted').show();
    else if (change == 'modified')
	    w.find('.modified').show();
    else if (change == 'tracked')
	    w.find('.tracked').show();
    else w.find('.unknown').show();
}


// exists icon
var exists = commit_row.get('row_exists');
if (exists == false) {
    w.find('.doesnt_exist').show();
}


// staged icon
var staged = commit_row.get('staged');
if (staged == true)
    w.find('.staged').show();
if (staged === false && change != 'same' && change != 'tracked')
    w.find('.unstaged').show();


var schema_name = commit_row.get('row_id').schema_name;
var relation_name = commit_row.get('row_id').relation_name;
var pk_column_name = commit_row.get('row_id').pk_column_name;
var pk_value = commit_row.get('row_id').pk_value;


// label setup -- FIXME when text_identifier is moved server-side
w.find('.label').html(schema_name+'/'+relation_name+'/'+pk_value);

// need this for insert
endpoint.schema(schema_name).table(relation_name).row(pk_column_name, pk_value).then(function(real_row) {
    w.find('.label').html(widget('ide:text_identifier', { row: real_row } ));

	// click handler needs real_row, so put the handler in here after real_row is loaded
    w.find('button.open').click(function() {
        w.trigger('bundle_row_open', { row_id: commit_row.get('row_id'), row: real_row, behind: true });
    });

    w.find('button.gank').click(function() {

        console.log(real_row);

        if (confirm('Copy this row into your project?')) {

            // clone and copy the row
            var cloned_row = real_row.clone();
            cloned_row.set('id', undefined);
            cloned_row.relation.insert(cloned_row.row_data)
            .then(function(new_row) {
                console.log('new_row',new_row);
                // call tracked_row_add()
                endpoint.schema('bundle').function('tracked_row_add', [
                    bundle.get('name'), // crap.  use id in tracked_row_add: FIXME in bundle
                    schema_name,
                    relation_name,
                    pk_column_name,
                    new_row.pk_value
                ]).then(function() {
                    w.trigger('status', 'Ganked a '+schema_name+'.'+relation_name);
                    w.trigger('reload_row_list');
                }).catch(function(e,o) {
                    alert('error tracking row!');
                    console.log('error ignoring row: ', row_id, e,o);
                });

            }).catch(function(err) {
                w.trigger('status', ['Failed save while copying from  ' + rel_name, 'fail']);
            });
        }

    });

    return real_row;

}).catch(function(e) {
    console.log(e);
	w.find('.label').html(schema_name+'.'+relation_name+'.'+pk_value);
}).then(function() {
    //w.show();
    w.find('.label > *').ready(function() {
        w.show();
    });
});

