.then(function(result) {
    var mounted = result.get('remote_is_mounted');
    if( mounted ) {
        undo = true;
        w.find('input.mounted').prop('checked',true);
        endpoint.schema('bundle').function('remote_is_online', [ remote.get('id') ])
        .then(function(result) {
            var online = result.get('remote_is_online');

            set_status(mounted, online);
            load_diff();
        });
    } else {
        set_status(false, false);
    }
