function set_status(mounted, online) {
    if( mounted ) {
        if( online ) {
            w.find('.online').show();
            w.find('.diff_container').show();
        }
        else {
            w.find('.offline').show();
            w.find('.diff_container').hide();
        }
    } else {
        w.find('.online').hide();
        w.find('.offline').hide();
        w.find('.diff_container').hide();
    }
}

