w.find('input.mounted').click(function(e) {
    console.log('processing change,checked = '+w.find('input.mounted').prop('checked'));

    // mount has been turned on
    if( $(this).prop('checked') ) {
        // try to mount it
        endpoint.schema('bundle').function('remote_mount', [ remote.get('id') ])
        .then(function(result) {
            if (result.get('remote_mount')) {
                set_status(true, true);
                load_diff();
