    var ps2 = Array();
    var start = 0;

    ps2.push({
        value: '[ none ]',
        data: 'none'
    });

    ++start;

    if (new_enabled) {
        ps2.push({
            value: '[ new ]',
            data: 'new'
        });

        ++start;
    }

    for (var i = 0; i < rows.length; i++) {

        ps2[start + i] = { value: label_function(rows[i]), data: rows[i] }

    }


    w.find('.autocomplete').autocomplete({
        lookup: ps2,
        appendTo: '.results',
        onSelect: function (suggestion) {
            //alert('You selected: ' + suggestion.value + ', ' + suggestion.data);
            select_row(suggestion.data);

        }
    });  

    load_complete();
});

