// This is sending the right thing over the wire, but PostgreSQL is using client_encoding
// to convert the string to the byte sequence of the UTF8 representation of the string.
// Which sucks.  A lot.  Fix endpoint.row_insert to check the type, see if it's binary,
// and call decode(content,'hex') on it.  But row_insert is such a mess that this is very hard.

w.find('button.upload').click(function () {
    const file = w.find('.fileUpload').get(0).files[0];
    var reader = new FileReader();

    reader.onload = function () {
        console.log(reader);
        function buf2hex(buffer) { // buffer is an ArrayBuffer
            return Array.prototype.map.call(new Uint8Array(buffer), x => ('00' + x.toString(16)).slice(-2)).join('');
        }
        var hex = buf2hex(reader.result);
        console.log(hex);

        endpoint.schema('endpoint').table('mimetype_extension').row({
            where: {
                name: 'extension',
                op: '=',
                value: file.name.split('.').pop()
            }
        }).then(function(ext) {
            const mimetype_id = ext.get('mimetype_id') || '';
            endpoint.schema('endpoint').table('resource_binary').insert({
                path: w.find('input.path').val(),
                content: hex,
                mimetype_id: mimetype_id
            }).then(result => {
                alert('success.');
                console.log('file saved')
                w.trigger('status', 'file saved.');
            }).catch(error => {
                alert('file upload failed:' + error)
            })
        }).catch(function(e) {
            alert('ERROR: Could not find mimetype for '+file.name.split('.').pop());
        });
    }

    reader.readAsArrayBuffer(file)
});

