var current_state = null;

function select_state() {

    var slugs = window.location.hash.split('/').slice(1);
    var new_state = null;
    if (slugs.length) {
        new_state = decodeURIComponent(slugs[0]);
    }

    if (new_state == null) {
        w.data('bundles', null);
        return show_bundles();
    }

    w.addClass('bundle_edit');
    if (new_state != current_state) {

        var bundles = new_state.split(',');
        w.data('bundles', bundles);

        w.find('> .bundle_workspace').empty().html(
            widget('dev_workspace', { base_url: '#/' + slugs[0], url_parts: slugs.slice(1) })
        );
        w.find('.bundle_manager_container').empty().append(
            widget('bundle', { bundles: bundles })
        );
        current_state = new_state;
    }

    $('html, body').animate({
        scrollTop: 0
    }, 'fast');

    // Do nothing if states are the same
    // Let the widget handle back/forward within itself

}
$(window).on('popstate', select_state);
select_state();


w.find('.select_bundles').on('click', show_bundles);



