var w = $("#"+id);

// creates a new tab
function new_tab(tab_id, label, content, behind) {
    // create the header
    w.find('.tab_headers').append(widget('ide:workspace_tab_header', {
        label: label,
        tab_id: tab_id,
        behind: behind
    }));


    // create the tab
    w.find('> .tab_contents').append(widget('ide:workspace_tab', {
        content: content,
        tab_id: tab_id,
        behind: behind
    }));

    // focus, or if behind is true, just highlight the tab
    if (!behind)
    	show_tab(tab_id);
    else
        behind_tab_header(tab_id);

}

function current_tab() {
    return w.find('.workspace_tab_header.highlighted').first().data('tab_id');
}

// brings tab to front
function show_tab(tab_id) {
    // show the tab
    w.find('.workspace_tab').each(function(e) {
        if ($(this).data('tab_id') == tab_id) {
            $(this).show();


			// divs created "behind" are not sizing correctly, hrm.
            if (typeof(Event) === 'function') {
                // modern browsers
                window.dispatchEvent(new Event('resize'));
            } else {
                // for IE and other old browsers
                // causes deprecation warning on modern browsers
                var evt = window.document.createEvent('UIEvents'); 
                evt.initUIEvent('resize', true, false, window, 0); 
                window.dispatchEvent(evt);
            }



        } else {
            $(this).hide();
        }
    })
    // highlight the tab header
    w.find('.workspace_tab_header').each(function(e) {
        if ($(this).data('tab_id') == tab_id) {
            $(this).addClass('highlighted');
            $(this).removeClass('behind');
        } else {
            $(this).removeClass('highlighted');
        }
    })
}

// sets the tab header's behind css selector to true
function behind_tab_header(tab_id) {
    // highlight the tab header
    w.find('.workspace_tab_header').each(function(e) {
        if ($(this).data('tab_id') == tab_id) {
            $(this).addClass('behind');
        }
    })
}

function close_tab(tab_id) {
    /*
    why doesn't this work?
    w.find('.workspace_tab[data-tab_id="'+tab_id+'"]').remove();
    w.find('.workspace_tab_header[data-tab_id="'+tab_id+'"]').remove();
    */

    var current_tab_closed = false;
    if (tab_id == current_tab()) {
        current_tab_closed = true;
    }

    // remove the tab
    w.find('.workspace_tab').each(function(e) {
        if ($(this).data('tab_id') == tab_id) {
            $(this).remove();
        }
    });

    // remove the tab header
    w.find('.workspace_tab_header').each(function(e) {
        if ($(this).data('tab_id') == tab_id) {
            $(this).remove();
        }
    });

    // if the current tab is closed, replace it with another
    if (current_tab_closed) {
        var last_tab = w.find('.workspace_tab').last();
        if (last_tab) {
            show_tab(last_tab.data('tab_id'));
        }
    }
}

function tab_is_open(tab_id) {
    // check to see if it's already open
    var is_open = false;
    var tabs = w.find('.workspace_tab');
    for (var i=0;i<tabs.length;i++) {
        var this_tab_id = $(tabs[i]).data('tab_id');

        // console.log('comparing '+this_tab_id +' to ' + tab_id);
        if (this_tab_id == tab_id) {
            // console.log('already open');
            return true;
        }
    };
    return false;
}

