function load_results(params) {
    var term = typeof (params.term) == 'undefined' ? '' : params.term;
    var method = typeof(params.method) == 'undefined' ? 'ilike' : params.method;
    var scope = typeof(params.scope) == 'undefined' ? 'stage' : params.scope;
    var bundle_id = typeof(params.bundle_id) == 'undefined' ? null : params.bundle_id;

    // term text, search_method search_method, scope search_scope, _bundle_id uuid 
    endpoint.schema('bundle').function('search', [ term, method, scope, bundle_id ])
    .then(function(results) {
        w.find('.loader').hide();
        w.find('.results td').remove();
        w.find('.results').append('<tr><td colspan=3>Found '+results.rows.length+' result.</td></tr>');

        if (results.rows.length == 0) {
            return;
        }
        if (results.rows.length == 1) {
            w.find('.results').append(widget('ide:search_result', { result: results }));
        } else {
            widget.sync(results, w.find('.results'), function(result) {
                return widget('ide:search_result', { result: result });
            });
        }
    }).catch(function(e) {
        w.find('.loader').hide();
        w.find('.results td').remove()
        w.find('.results').append('<tr><td colspan=3>Search error: '+e+'</td></tr>');

    });
}

