/***************************************************************************

WIDGET TREE TRAVERSAL FUNCTIONS

***************************************************************************/
// returns a object tree that mirrors the widget tree
function get_widget_tree(element) {
    var widget_node = {};

    // first (non-widget?) element
    if (!$(element).attr('data-widget')) {
        widget_node.name = "[non-widget]";
        widget_node.bundle_name = "[non-widget]";
        widget_node.bundle_alias = "[non-widget]";
        widget_node.widget_id = "[non-widget]";
        widget_node.widget_row_id = "[non-widget]";
        widget_node.id = 'n/a';
    } else {
        widget_node.name = $(element).attr('data-widget');
        widget_node.id = $(element).attr('id');
        widget_node.bundle_name = $(element).attr('data-bundle_name');
        widget_node.bundle_alias = $(element).attr('data-bundle_alias');
        widget_node.widget_id = $(element).attr('data-widget_id');
        widget_node.widget_row_id = $(element).attr('data-widget_row_id');
        widget_node.event_listeners = $._data($(element)[0],'events');
        //console.log('event listeners: ', widget_node.event_listeners);
        widget_node.element = element;
    }

    var children = {}
    $(element).closestDescendant('[data-widget]', true).each(function(i,e) {
        children[i] = get_widget_tree($(e));
    });
    widget_node.children = children;

    return widget_node;
}

// returns an object tree that combines parallel widgets of the same name, with a count var
function get_aggregate_widget_tree(bigtree) {
    var smalltree = {}
    if (bigtree.children) {
        for (var i in bigtree.children) {
            var child = bigtree.children[i];
            var name = child.name;
            var children = get_aggregate_widget_tree(child); 

            smalltree[name] = smalltree[name] ? smalltree[name] : {};

            // SETUP PROPERTIES

            // count
            smalltree[name]['count'] = smalltree[name]['count'] ? smalltree[name]['count'] +1 : 1;

            // ids
            if (typeof smalltree[name]['ids'] != 'undefined') {
                smalltree[name]['ids'].push(child.id);
            } else {
                smalltree[name]['ids']=Array(child.id);
            }

            // bundle stuff
            smalltree[name]['bundle_name'] = child.bundle_name;
            smalltree[name]['bundle_alias'] = child.bundle_alias;
            smalltree[name]['widget_row_id'] = child.widget_row_id;
            smalltree[name]['event_listeners'] = child.event_listeners;

            // children
            if (typeof smalltree[name]['children'] != 'undefined' && smalltree[name]['children'].length > 0) {
                console.log("EDGE CASE!", smalltree[name]['children']);
                // TODO: there's an edge case to flesh out here, with inter-woven widget trees
                // console.log('need to add ', children, ' to ', kids[name][children]);
            } else {
				smalltree[name]['children'] = children;
            }
        }
    }
    return smalltree;
}

