endpoint.schema('widget').table('widget').row('id', row.get('id'))
.then(function(row) {

    w.find('.name').append(widget('semantics/form_field', row.field('name')));

    /* SETUP */
    var rel_name = row.relation.schema.name + '.' + row.relation.name;
    var relation = row.relation;

    var tabs = {};
    var tab_colors = {};
    var colors = ['red', 'orange', 'yellow', 'green', 'blue', 'indigo', 'violet'];

	// html, css, post_js, server_js
    ['html','css',/*server_js','common_js'*/,'post_js'].forEach(function(col_name, i) {
        tabs[col_name] = function() {
            return widget('semantics/form_field', row.field(col_name));
        };
        tab_colors[col_name] = colors[i % colors.length];
    });

    // input
    tabs['input'] = function() {
        return widget('semantics/grid_view', endpoint.schema('widget').table('input').rows({
        where: {
            name: 'widget_id',
            op: '=',
            value: row.get('id')
        },
            exclude: ['id', 'widget_id']
        }, {
            context: {
                widget_id: row.get('id')
            }
        }));
    };
    tab_colors['input'] = colors[5 % colors.length];

    // dependency_js
    tabs['dependencies'] = function() {
        return widget('ide:widget_dependencies_editor', { widget_row: row });
        /*
        return widget('semantics/grid_view', endpoint.schema('widget').table('widget_dependency_js').rows({
            where: {
                name: 'widget_id',
                op: '=',
                value: row.get('id')
            },
            include: ['dependency_js_id']
        }, {
            context: {
                widget_id: row.get('id')
            }
        }));
        */
    };
    tab_colors['dependencies'] = colors[6 % colors.length];


    /* TABBED LAYOUT */
    // var preview_row = row.clone();

    w.find('> .tabs').append(
        widget('ide:tabbed_layout', {
            tabs: tabs,
            tab_colors: tab_colors
        })
    );

    function handle_delete_button() {
        if(confirm("Are you sure you\'d like to delete this row?")) {
            row.delete()
            .then(function() {
                w.trigger('status', ['Widget deleted', 'success']);
                w.trigger('done');
                w.trigger('tab_close_self');
            }).catch(function() {
                w.trigger('status', ['Failed delete on ' + rel_name, 'fail']);
            });
        }
    }


    function set_have_unsaved_changes(is_unsaved){
        /*
    var save_button = w.find("> .save");
    save_button.text(is_unsaved ? 'Save' : 'Saved');
    window.onbeforeunload = is_unsaved ? function() { return "You have unsaved changes!" } : null;
    */
    } 



    function handle_save_button(e) {

        update_row()
        .then(function() {
            //set_have_unsaved_changes(false);
            w.trigger('status', ['Widget saved', 'success']);
            w.trigger('row_detail_save');
            w.trigger('reload_row_list');
        }).catch(function() {
            w.trigger('status', ['Failed save on ' + rel_name, 'fail']);
            //setTimeout(set_have_unsaved_changes.bind(this,false), 2000)
        });
        // ctrl+s saves a webpage normally so stop that.
        e.preventDefault();
        // stop this event from propagating down to other widgets potentially
        e.stopPropagation();


    }



    function update_row() {

        row.set('pre_js',  row.get('pre_js'));
        row.set('css',     row.get('css'));
        row.set('html',    row.get('html'));
        row.set('post_js', row.get('post_js'));

        return row.update();

    }

    w.find('.save').on('click', handle_save_button);
    w.on('keydown', jwerty.event('ctrl+s', handle_save_button));
    w.on('keydown', jwerty.event('ctrl+alt+s', handle_save_button));
    w.find('.delete').on('click', handle_delete_button);

    w.find('.copy').click(function() {

        var new_name;
        if (new_name = prompt('Name for new row (previously '+row.get('name')+')')) {

            var cloned_row = row.clone();
            cloned_row.set('id', undefined);
            cloned_row.set('name', new_name);


            relation.insert(cloned_row.row_data)
            .then(function(new_row) {
                w.trigger('status', ['Widget copy saved', 'success']);
            }).catch(function(err) {
                w.trigger('status', ['Failed save while copying from  ' + rel_name, 'fail']);
            });
        }

    });
});

