function search(e) {
    var list_items = w.find('li');
    var val = $(this).val().trim();
    if (val != '') {

        var matching_elements = $.grep(
            list_items, 
            function(e, i) {
                var re = new RegExp(val, 'i');

                if (re.test($(e).html())) {
                    return true;
                }
                return false;
            });

        list_items.hide();
        $(matching_elements).show();
    }
    else {
        list_items.show();
    }
}
