    logout_and_redirect();
});



w.find('.logout').click(function() {

    var cookie = get_session_cookie('SESSION');
    if (cookie != null) {

        // there exists a session cookie
        // look for session in table
        endpoint.schema('endpoint').table('session').row('id', cookie)
        .then(function(session) {

            if(!session) {
                // not logged in
                return logout_and_redirect();
            }
            session.delete().then(function() {
                // logged out now
                // redirect
                logout_and_redirect();
            });
        });

    }
    else {
        // there is no session cookie
        logout_and_redirect();
    }
});

