function reload() {
    w.find('.loader_item').show();
    w.find('.offstage_stat').hide();
    w.find('.stage_stat').hide();
    w.find('div.diff_item').hide();
    w.find('div.stage_row_item').hide();
    w.find('.no_change_item').hide();



    endpoint.schema('bundle').view('head_db_stage_changed').row('(row_id).pk_value', row.id.pk_value).then(function(change) {
        w.find('.loader_item').hide();

        if(!change) {
  		  	w.find('.no_change_item').html("No changes.").show();
            return;
        }

        // add stage_dialog_changed_row (we're reusing this from stage)

        w.find('.stage_row_item').show().html(widget('ide:stage_dialog_changed_row', { bundle: bundle, change: change }));



        // diff button click handler

        w.find('.diff_item > button.diff_button').unbind('click').click(function() {
            w.append(widget('ide:modal', {
                content: widget('ide:stage_row_changed_diffs', {
                    change: change,
                    bundle: bundle
                })
            }));
        });



        // propagate staged fields

        var offstage_changed_columns = Array();
        var stage_changed_columns = Array();
        for(var i=0;i<change.get('offstage_field_changes').length;i++) {
            w.find('.offstage_stat').show();
            w.find('div.diff_item').show();
            offstage_changed_columns.push(change.get('offstage_field_changes')[i].column_name);
        }
        for(var i=0;i<change.get('stage_field_changes').length;i++) {
            w.find('div.diff_item').show();
            w.find('.stage_stat').show();
            stage_changed_columns.push(change.get('stage_field_changes')[i].column_name);
        }

        w.find('.stage_changes').html(stage_changed_columns.join(', '));
        w.find('.offstage_changes').html(offstage_changed_columns.join(', '));

    }).catch(function(e) {
        console.error(e);
        w.find('.loader_item').hide();
        w.find('.no_change_item').show().html('New row.');
    });
}
