.then(function(row) {

    w.find('.section_content').append(widget('semantics/form_field', row.field('content')));

    w.find('.path').append(widget('semantics/form_field', row.field('path')));
    w.find('.path_link').attr('href', row.get('path'));
    w.find('.path_button').text(row.get('path'));

    // SAVE
    function save(e) {
        row.update()
        .then(function() {
            w.trigger('row_detail_save');
            w.trigger('status', 'Resource saved');
            w.trigger('reload_row_list');
        }).catch(function(err) {
            w.trigger('status', 'Failed save on ' + rel_name + '<br>' + err);
        });
        e.preventDefault();
        e.stopPropagation();
    }


    w.find('.save').on('click', save);
    w.on('keydown', jwerty.event('ctrl+s', save));


    // DELETE
    w.find('.delete').on('click', function() {
        if(confirm("Are you sure you\'d like to delete this row?")) {
            row.delete()
            .then(function() {
                w.trigger('status', 'Resource deleted');
                w.trigger('reload_row_list');
                w.trigger('done');
                w.trigger('close_tab_self');
                w.remove();
            }).catch(function(e) {
                console.error(e);
                w.trigger('status', 'Failed row delete on ' + rel_name);
                w.trigger('done');
            });
        }
    });



    // COPY
    w.find('.copy').click(function() {

        if (confirm('Copy this row?')) {

            var cloned_row = row.clone();
            var new_path = row.get('path') + '_COPY';

            cloned_row.set('id', undefined);
            cloned_row.set('path', new_path);

            row.relation.insert(cloned_row.row_data)
            .then(function(new_row) {
                w.trigger('status', 'Resource copy saved');
                if (confirm('Would you like to edit the new row now?\nPress cancel to resume editing original')) {
                    w.replaceWith(
                        widget('semantics/row_detail', new_row)
                    );
                }
            }).catch(function(err) {
                w.trigger('status', 'Failed save while copying from  ' + rel_name + ':<br>' + err);
            });
        }

    });

    endpoint.schema('endpoint').table('mimetype').rows({ order_by: { column: 'mimetype' }}).then(function(mimetypes) {
        widget.sync(mimetypes, w.find('.mimetype select'), function(m) {
            return widget('tag:option', { label: m.get('mimetype'), value: m.get('id'), row: m });
        });

        w.find('.mimetype select').change(function(e) {
            row.set('mimietype_id', $(this).val());

        });
    });
});

