.then(function(row) {

    w.find('.section_content').append(widget('semantics/form_field', row.field('content')));

    w.find('.name').append(widget('semantics/form_field', row.field('name')));

    w.find('.version').append(widget('semantics/form_field', row.field('version')));

    // SAVE
    function save() {
        row.update()
        .then(function() {
            w.trigger('status', ['Dependency saved', 'success']);
        }).catch(function() {
            w.trigger('status', ['Failed save on ' + rel_name, 'fail']);
        });
    }


    w.find('.save').on('click', save);
    w.on('keydown', jwerty.event('ctrl+s', save));


    // DELETE
    w.find('.delete').on('click', function() {
        if(confirm("Are you sure you\'d like to delete this row?")) {
            row.delete()
            .then(function() {
                w.trigger('status', ['Dependency deleted', 'success']);
                w.trigger('row_detail_delete');
                w.trigger('done');
                w.remove();
            }).catch(function() {
                w.trigger('status', ['Failed row delete on ' + rel_name, 'fail']);
                w.trigger('done');
            });
        }
    });



    // COPY
    w.find('.copy').click(function() {

        if (confirm('Copy this row?')) {

            var cloned_row = row.clone();
            var new_name = row.get('name') + '_COPY';

            cloned_row.set('id', undefined);
            cloned_row.set('name', new_name);

            row.relation.insert(cloned_row.row_data)
            .then(function(new_row) {
                w.trigger('status', ['Dependency copy saved', 'success']);
                if (confirm('Would you like to edit the new row now?\nPress cancel to resume editing original')) {
                    w.replaceWith(
                        widget('semantics/row_detail', new_row)
                    );
                }
            }).catch(function(err) {
                w.trigger('status', ['Failed save while copying from  ' + rel_name, 'fail']);
            });
        }

    });

});

/*
 * ARGUMENTS
 * -----------
 * context_data: {Object} for compatibility with sem_list_item_new
 * from_row: {Row} row to join
 * from_column: {String} fkey column of from_row
 * to_relation: {Table} relation to join to
 * to_column: {String} column of to_relation fkey references
 * order_by_function: {Function}
 *	- takes a rowset and returns the rowset after performing order_by clause
 * label_function: {Function}
 *	- takes a ‘to_row’ and returns the human-readable label
 * new_enabled: {Boolean} whether to include 'new row' functionality
 */

var w = $("#"+id);

if (typeof context_data == 'undefined') {
    context_data = {};
}

if (typeof order_by_function == 'undefined') {
    order_by_function = function(rows) {
        return rows;
    };
}

if (typeof label_function == 'undefined' || label_function == false) {
    label_function = function(to_row) {
        return to_row.get('name');
    };
}

function load_options() {
    var content = "<option value='none' selected>[ None ]</option>";

    if (typeof new_enabled != 'undefined' && new_enabled == true) {
        content += "<option class='new' value='new'>[ Create New ]</option>";
    }

    w.find('select').empty().html(content);

    var to_rows = order_by_function(to_relation.rows());
    widget.sync(to_rows, w.find('select'), function(to_row) {
        return widget('option', {
            label: label_function(to_row),
            value: to_row.get(to_column),
            selected: from_row.get(from_column) == to_row.get(to_column)
        });
    });
}
