var w = $("#"+id);

var old_count, count;

function setCount() {
    w.find('.count').html('<i class="spinner icon"></i>');
    endpoint.schema('bundle').view('untracked_row').rows().then(function(rows) {
        w.find('.count').html(rows.length);

        old_count = typeof w.data('count') != 'undefined' ? w.data('count') : -1;
        w.data('count', rows.length);

        if(old_count > 0 && old_count != w.data('count') ) {
            w.find('.count').html(count);
            w.find('.count').fadeIn(100).fadeOut(100).fadeIn(100).fadeOut(100).fadeIn(100);

            if(old_count < w.data('count')) {
                w.trigger('status', "💩 " + (w.data('count') - old_count) + " untracked rows.");
            } 
            if(old_count > w.data('count')) {
                w.trigger('status', "👍 " + (old_count - w.data('count')) + " untracked rows resolved.");
            } 
        }
        // w.trigger('untracked_count_updated');  I'M DISABLING THE AUTO_REFRESH BECAUSE IT IS ANNOYING
    });
}

w.bind('untracked_count_updated', function() {
    setTimeout(setCount, 15000);
});

setCount();


