var w = $("#"+id);

// remote has, local doesn't
if (diff_row.get('a_bundle_id') == null) {

    w.find('.remote')
    .show()
    .append(diff_row.get('b_name'));

    w.find('button.download').show();
}
// local has bundle, remote doesn't
else if (diff_row.get('b_bundle_id') == null) {

    w.find('.local')
    .show()
    .append(diff_row.get('a_name'));

    w.find('button.upload').show();
} // remote has bundle, local doesn't
else {
    w.find('.local').append(diff_row.get('a_name'));
    w.find('.remote').append(diff_row.get('b_name'));

    // FIXME

    var a_commits = diff_row.get('a_commits');
    var b_commits = diff_row.get('b_commits');


    // search b_commits for commits not in a_commits
    var a_commits_diff = Array();
    var a_in_sync = true;
    for (var i=0;i<a_commits.length;i++) {
        var commit = a_commits[i];
        var present = false;
        for (var j=0;j<b_commits.length;j++) {
            // console.log('comparing', commit, b_commits[j]);
            if (commit.id == b_commits[j].id) {
                present = true;
            }
        }
        if (!present) {
            a_commits_diff.push(commit);
            a_in_sync = false;
        }
    }

    // search b_commits for commits not in a_commits
    var b_commits_diff = Array();
    var b_in_sync = true;
    for (var i=0;i<b_commits.length;i++) {
        var commit = b_commits[i];
        var present = false;
        for (var j=0;j<a_commits.length;j++) {
            // console.log('comparing', commit, b_commits[j]);
            if (commit.id == a_commits[j].id) {
                present = true;
            }
        }
        if (!present) {
            b_commits_diff.push(commit);
            b_in_sync = false;
        }
    }

    if (a_in_sync && b_in_sync) {
	    w.addClass('insync');
        // w.find('.diff').html('fully in-sync');
    } else {

        if (!a_in_sync) {
            w.find('.local').append(widget('ide:bundles_mgr_remote_summary_commits_diff', {
                commits: a_commits_diff

            }));
            w.find('.local button.push').show();
        }
        if (!b_in_sync) {
            w.find('.remote').append(widget('ide:bundles_mgr_remote_summary_commits_diff', {
                commits: b_commits_diff 
            }));
            w.find('.remote button.pull').show();
        }
    }
}

