//endpoint.schema('widget').table('widget').row('name', 'dev_workspace').then(rewrite);
function rewrite(r) {

    function get_data(str) {
        // If this does not have 'g' flag there will be an infinite loop
        var re = /\/\*editable_object-(.*)\*\/([\s\S]*?)\/\*end\*\//g,
            arr,
            obj = {};

        // Safety measure
        var MAX = 5,
            counter = 0;

        while ((arr = re.exec(str)) !== null) {
            if (counter++ > MAX) {
                break;
            }
            /* arr[0] is whole match; arr[1] is object identifier; arr[2] is object data only */
            obj[arr[1]] = eval(arr[2]);
        }
        return obj;
    }

    function replace(str, ident, new_data) {
        var re = new RegExp('(\/\\*editable_object-' + ident + '\\*\/)([\\s\\S]*)(\/\\*end\\*\/)');
        return str.replace(re, '$1' + JSON.stringify(new_data, null, '\t') + '$3');
    }

    var js = r.get('post_js');
    var data = get_data(js);
    var new_js = replace(js, 'tables', data.tables.filter(function(e) {
        return !(e.schema_name == 'widget' && e.relation_name == 'widget');
    }));

    // r.set('post_js', new_js).update().then(function(a) { console.log(a); });
}

