var tabs = {};
var tab_colors = {};
tables.forEach(function(t, i) {
    var schema_name = t.schema_name;
    var name = t.relation_name;
    var relation = endpoint.schema(schema_name).table(name);

    var obj = {};
    var keys = Object.keys(t);
    keys.forEach(function(o) {
        if (o != 'relation_name' && o != 'schema_name') {
            obj[o] = t[o];
        }
    });

    tabs[name] = function() {
        if (!Object.keys(obj).length) {
            return widget('semantics/list_view', relation);
        }
        return widget('semantics/list_view', relation.rows(obj));
    };

    tab_colors[name] = colors[i % colors.length];
});

